insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.1.6.2', 'cae', '5.2.1.6.2', 'Orienter les dépenses budgétaires grâce à l’analyse des marges de manœuvre tirée de l’évaluation climat du budget', '<p>La part du budget « favorable » ou « très favorable » d’un point de vue climatique est en hausse</p>
<p>La part du budget « défavorable » d’un point de vue climatique est en baisse</p>
', '', '', '', '', '', null, null, null);
