insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.1.7.1', 'cae', '5.2.1.7.1', 'Apporter du soutien financier à des thématiques environnementales variées, à des cibles variées et sous différentes formes', '<p>Thématiques environnementales : bâtiments, énergie, air, eau, agriculture, ENR, mobilité...</p>
<p>Cibles : particuliers, association, entreprises, agriculteurs...</p>
<p>Formes du soutien financier : partenariat avec des banques, tiers-investissement, fonds de garantie, fonds d''investissement, prise de participation dans des SCIC/SEM œuvrant pour le DD ou des SAS/SA portant des projets de production d’énergie renouvelable sur leur territoire...</p>
', '', '', '', '', '', null, null, null);
