insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.2.5.1', 'cae', '5.2.2.5.1', 'Connaître et formaliser dans un document (listing) l''étendue de l''offre locale pour répondre aux besoins de la collectivité (producteurs, fournisseurs...)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.2.5.2', 'cae', '5.2.2.5.2', 'Identifier dans cette délibération, les pratiques d''achats et les améliorations à apporter', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.2.5.3', 'cae', '5.2.2.5.3', 'Détailler les directives pour les achats dans les domaines du matériel du bureau, entretien des bâtiments, véhicules et mobilité, matériaux de construction', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.2.6', 'cae', '5.2.2.6', 'Appliquer des clauses environnementales variées et systématiser la logique cycle de vie des produits et services, afin de déterminer les dispositions environnementales pertinentes selon les familles d''achat', '', '', '', '', '', '', null, 20.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.2.6.1', 'cae', '5.2.2.6.1', 'Inviter les candidats à répondre aux appels d''offres de manière électronique', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.2.6.2', 'cae', '5.2.2.6.2', 'Évaluer les fournisseurs sur l’application de leurs engagements environnementaux pendant le déroulement de leur prestation (par exemple prestataires de restauration collective ou travaux)', '<p>Exemple : La pondération des critères environnementaux est au moins égale à 10% de la note globale d’un marché et se fonde sur des éléments vérifiables (ex : note de frais d’aliments bio et locaux, remboursement de billets de train, etc.) que l’attributaire devra remettre au commanditaire</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.2.6.3', 'cae', '5.2.2.6.3', 'Encourager la fourniture du bilan GES des produits/services achetés dans les appels d’offres', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.2.6.4', 'cae', '5.2.2.6.4', 'Effectuer l''entretien des bâtiments et des espaces publics selon des critères écologiques', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.2.6.5', 'cae', '5.2.2.6.5', 'Effectuer le choix des matériaux de construction selon des critères écologiques (le bois est notamment systématiquement étudié) et repose sur les gisements locaux (Pisé, paille, etc.)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.2.6.6', 'cae', '5.2.2.6.6', 'Choisir les techniques et matériaux de finition, ainsi que le mobilier pour limiter la pollution de l''air intérieur (faibles émissions de COV et formaldéhydes notamment), soit en privilégiant des produits avec étiquette A+, soit en achetant des produits d’occasion', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.2.6.7', 'cae', '5.2.2.6.7', 'Inclure la prise en compte de la durée de vie et de la fin de vie des produits achetés dans les critères d’achats', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.2.7', 'cae', '5.2.2.7', 'Participer à l''amélioration de l''offre en services et produits responsables / entraîner les acteurs locaux', '', '', '', '', '', '', null, 5.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.2.7.1', 'cae', '5.2.2.7.1', 'Informer les fournisseurs et producteurs de la politique d’achats responsables par une communication ciblée auprès des entreprises concernées, voire via la plateforme d’achat de la collectivité', '', '', '', '', '', '', null, null, null);
